n=20000;
v=3;
m0=10;
randn('state',123456);
Y=randn(n,v);
% 25 per cent of Contaminated data
Ycont=Y;
Ycont(1:5000,:)=Ycont(1:5000,:)+3;
[fre]=unibiv(Y);
fre=sortrows(fre,[3 4]);
bs=fre(1:m0,1);
[mmd,Un,BB]=FSMmmd(Ycont,bs,'plots',1);
