n=200;
v=3;
m0=4;
randn('state',123456);
Y=randn(n,v);
%Contaminated data
Ycont=Y;
Ycont(1:5,:)=Ycont(1:5,:)+3;
[fre]=unibiv(Y);
fre=sortrows(fre,[3 4]);
bs=fre(1:m0,1);
mmd=FSMmmdeasy(Ycont,bs);
plot(mmd(:,1),mmd(:,2))

