XX=load('wool.txt');
y=XX(:,end);
X=XX(:,1:end-1);
% Score test using the five most common values of lambda
[outSc]=Score(y,X);

load('loyalty.txt');
y=loyalty(:,4);
X=loyalty(:,1:3);
% la = vector containing the values of the transformation
% parameters which have to be tested
la=[0.25 1/3 0.4 0.5];
[outSc]=Score(y,X,'la',la,'intercept',0);
